import React, { useEffect, useState } from "react";
import { Layout, Row } from "antd";
import { useRouter } from "next/router";
import { connect } from "react-redux";

function foodDetail(props) {
  const router = useRouter();
  const fid = router.query.fid;
  console.log(router.query.fid);

  //   const [data, setdata] = useState({});

  //   useEffect(() => {
  //     setdata(props.food.filter((el) => el._id === fid));
  //     console.log(data);
  //   });

  return (
    <div>
      <Layout>
        <Row>
          <p>Post:{fid}</p>
        </Row>
        <Row>
          {props.food
            .filter((el) => el._id === fid)
            .map((el) => {
             ( <div>
                <p>title:{el.title}</p>
                <p>kitchenName:{el.KitchenName}</p>
                <p>meals:{el.meals}</p>
              </div>);
            })}
        </Row>
        <Row></Row>
        <Row></Row>
      </Layout>
    </div>
  );
}
const mapStateToProps = (state) => ({
  food: state.item.items,
});

export default connect(mapStateToProps, null)(foodDetail);
