import React, { useEffect, useState } from "react";
import { Avatar, Row, Menu, Radio, Badge, Switch } from "antd";
import UserLayout from "./userlayout";
import { Card, Input, Button } from "antd";
import { SearchOutlined } from "@ant-design/icons";
import Styles from "../styles/food.module.css";
import { connect } from "react-redux";
import { getItems } from "../Actions/ItemsAction";
const { Meta } = Card;

function Food() {
  const [value, setvalue] = useState(1);
  const [state, setState] = useState({ meals: "", mealType: "" });

  const onchange = (e) => {
    setvalue(e.target.value);
  };

  const handleChange = (event) => {
    console.log(e);
  };

  return (
    <div>
      <UserLayout>
        <Row style={{ marginTop: "75px", justifyContent: "center" }}>
          <div className="site-card-border-less-wrapper">
            <Card style={{ borderLeft: "3px solid #74cf4e" }}>
              <h3>All deliveries</h3>

              <Row
                style={{
                  flexDirection: "row",
                  width: "600px",
                  alignItems: "center",
                }}
              >
                <Input
                  shape="round"
                  style={{ width: "250px", borderRadius: "21px" }}
                  placeholder="input search text"
                  //   enterButton="Search"
                  size="large"
                  //  onSearch={(value) => console.log(value)}
                />
                <Button
                  shape="round"
                  style={{
                    marginLeft: "46px",
                    backgroundColor: "#74cf4e",
                    color: "white",
                    borderColor: "#74cf4e",
                    border: 0,
                  }}
                  icon={<SearchOutlined />}
                >
                  Search
                </Button>
                <Switch
                  onClick={handleChange}
                  style={{
                    marginLeft: " 80px",
                    backgroundColor: "#74cf4e",
                    // width: " 77px",
                  }}
                  checkedChildren="veg"
                  unCheckedChildren="Non-veg"
                  defaultChecked
                />
              </Row>
              <Row>
                <Menu
                  mode="horizontal"
                  // style={{ boxShadow: "1px 4px 6px #888888", borderRadius: " 30px" }}
                  defaultSelectedKeys={["2"]}
                >
                  <Menu.Item key="1" style={{ height: "58px" }}>
                    <p> Breakfast</p>
                  </Menu.Item>
                  <Menu.Item key="2" style={{ height: "58px" }}>
                    <p>Lunch</p>
                  </Menu.Item>
                  <Menu.Item key="3" style={{ height: "58px" }}>
                    <p>Dinner</p>
                  </Menu.Item>
                  <Menu.Item key="4" style={{ height: "58px" }}>
                    <p>Snacks</p>
                  </Menu.Item>
                </Menu>
              </Row>
              <Row
                style={{
                  justifyContent: "space-between",
                  width: "600px",
                  flexFlow: "row wrap",
                  marginTop: "10px",
                }}
              >
                <Card
                  hoverable
                  className="Styles.antCardBody"
                  bodyStyle={{ padding: "0", fontSize: "9px" }}
                  style={{ width: 117 }}
                  cover={<img alt="example" src="./images/plate.jpg" />}
                >
                  <p>Roti,Daal,Rice</p>
                  <p>Special 1 plate</p>
                </Card>

                <Badge status="success">
                  <Card
                    hoverable
                    className="Styles.antCardBody"
                    bodyStyle={{ padding: "0", fontSize: "9px" }}
                    style={{ width: 117 }}
                    cover={<img alt="example" src="./images/plate.jpg" />}
                  >
                    {" "}
                    <p>Roti,Daal,Rice</p>
                    <p>Special 1 plate</p>
                  </Card>
                </Badge>
                <Card
                  hoverable
                  className="Styles.antCardBody"
                  bodyStyle={{ padding: "0", fontSize: "9px" }}
                  style={{ width: 117 }}
                  cover={<img alt="example" src="./images/plate.jpg" />}
                >
                  <p>Roti,Daal,Rice</p>
                  <p>Special 1 plate</p>
                </Card>
                <Card
                  hoverable
                  className="Styles.antCardBody"
                  bodyStyle={{ padding: "0", fontSize: "9px" }}
                  style={{ width: 117 }}
                  cover={<img alt="example" src="./images/plate.jpg" />}
                >
                  <p>Roti,Daal,Rice</p>
                  <p>Special 1 plate</p>
                </Card>
                <Card
                  hoverable
                  className="Styles.antCardBody"
                  bodyStyle={{ padding: "0", fontSize: "9px" }}
                  style={{ width: 117 }}
                  cover={<img alt="example" src="./images/plate.jpg" />}
                >
                  <p>Roti,Daal,Rice</p>
                  <p>Special 1 plate</p>
                </Card>
                <Card
                  hoverable
                  className="Styles.antCardBody"
                  bodyStyle={{ padding: "0", fontSize: "9px" }}
                  style={{ width: 117 }}
                  cover={<img alt="example" src="./images/plate.jpg" />}
                >
                  <p>Roti,Daal,Rice</p>
                  <p>Special 1 plate</p>
                </Card>
              </Row>
              {/* <Row style={{ marginTop: "12px" }}>
                <p>
                  <b style={{ marginRight: "7px" }}>Vender:</b>Naman
                </p>
              </Row> */}
              {/* <Orders /> */}
            </Card>
          </div>
        </Row>
        <Row style={{ justifyContent: "center", marginTop: "15px" }}>
          <div className="site-card-border-less-wrapper">
            <Card style={{ borderLeft: "3px solid #74cf4e", width: " 652px" }}>
              <h3>Choose Subscription Plan</h3>
              <div>
                <Radio.Group onChange={onchange} value={value}>
                  <Radio className={Styles.radioStyle} value={1}>
                    3 Days Subscription Plan
                  </Radio>
                  <Radio className={Styles.radioStyle} value={2}>
                    6 Days Subscription Plan
                  </Radio>
                  <Radio className={Styles.radioStyle} value={3}>
                    14 Days Subscription Plan
                  </Radio>
                  <Radio className={Styles.radioStyle} value={4}>
                    28 Days Subscription Plan
                  </Radio>
                  {/* <Radio style={radioStyle} value={4}>
                    More...
                    {value === 4 ? (
                      <Input style={{ width: 100, marginLeft: 10 }} />
                    ) : null}
                  </Radio> */}
                </Radio.Group>
              </div>
              <Button
                //   type="primary"
                shape="round"
                //icon={<DownloadOutlined />}
                size="large"
                style={{
                  marginLeft: "246px",
                  marginTop: "39px",
                  backgroundColor: "#74cf4e",
                  color: "white",
                }}
                value="Silver"
                id="silver"
                //onClick={choosePlan}
              >
                Confirm
              </Button>
            </Card>
          </div>
        </Row>
      </UserLayout>
    </div>
  );
}

export default connect(null, { getItems })(Food);
