import { Order_Food, Order_Add, defaultAxios } from "./Types";
import { useRouter } from "next/router";

export const Orderfood = (data) => (dispatch) => {
  console.log(data);
  dispatch({
    type: Order_Food,
    payload: data,
  });
};

export const OrderAddress = (id, data) => (dispatch) => {
  console.log(data);
  defaultAxios.put(`/user/cart/${id}`, data).then((res) =>
    dispatch({
      type: Order_Add,
      payload: data,
    })
  );
};
export const shipFood = (data) => (dispatch) => {
  // const router = useRouter();
  defaultAxios.get(`/venders/${data}`).then((res) => {
    console.log(Router);
   
    dispatch({
      type: "Get_Cart",
      payload: res.data,
    });
  });
};

export const Createcart = (data) => (dispatch) => {
  console.log(data);
  const config = {
    headers: {
      "Content-Type": "application/json",
    },
  };

  defaultAxios.post(`/user/cart`, data, config).then((res) =>{
    
    Router.push("/[shipping]", `/${res.data._id}`);
    dispatch({
      type: "Add_cart",
      payload: res.data,
    })}
  );
};
