import { GET_ITEMS, Post_Plan, Post_Add } from "./Types";
import Axios from "axios";

export const getItems = (data) => (dispatch) => {
  console.log(data);
  Axios.get(`http://localhost:5000/venders?veg=${data.veg}&meals=${data.meals}`
  ).then((res) =>
    // dispatch({
    //   type: GET_ITEMS,
    //   payload: res.data,
    // })
    {
      console.log(res);
    }
  );
};

export const postPlan = (data) => (dispatch) => {
  console.log(data, "ddd");
  dispatch({
    type: Post_Plan,
    payload: data,
  });
};
export const postAddress = (data) => (dispatch) => {
  dispatch({
    type: Post_Add,
    payload: data,
  });
};
