import React, { Component } from "react";
import { Carousel, Row, Col } from "antd";
import { LeftCircleOutlined, RightCircleOutlined } from "@ant-design/icons";
export default class CarouselComponent extends Component {
  constructor(props) {
    super(props);
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
    this.carousel = React.createRef();
  }
  next() {
    this.carousel.next();
  }
  previous() {
    this.carousel.prev();
  }

  render() {
    const props = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
    };
    return (
      <div>
       
        {/* <Icon type="left-circle" /> */}
        <Carousel ref={(node) => (this.carousel = node)} {...props} autoplay>
          <div>
            {/* <h3>1</h3> */}
            <Row>
              <Col className="chefContainer">
                <img className="chef" src="./images/chef1.jpg" />
              </Col>
              <Col>rahul</Col>
            </Row>
          </div>
          <div>
            <Row>
              <Col className="chefContainer">
                <img className="chef" src="./images/chef2.jpeg" />
              </Col>
              <Col>rahul</Col>
            </Row>
          </div>
          <div>
            <Row>
              <Col className="chefContainer">
                <img className="chef" src="./images/chef3.jpg" />
              </Col>
              <Col>rahul</Col>
            </Row>
          </div>
        </Carousel>
        <Row>
            <Col></Col>
            <Col>  <RightCircleOutlined onClick={this.next} /></Col>
        </Row>
       
        {/* <Icon type="right-circle"  /> */}
      </div>
    );
  }
}
