import React, { useEffect, useState } from "react";
import { Avatar, Row, Menu, Radio, Badge, Switch, Modal } from "antd";
import UserLayout from "./userlayout";
import { Card, Input, Button } from "antd";
import { SearchOutlined } from "@ant-design/icons";
import Styles from "../styles/food.module.css";
import { connect } from "react-redux";
import Link from "next/link";
import { getItems } from "../Actions/ItemsAction";
const { Meta } = Card;

function Food(props) {
  const [state, setState] = useState({ meals: "lunch", veg: "true" });
  const [modalProps, setmodalProps] = useState({
    loading: false,
    visible: false,
  });

  showModal = () => {
    setmodalProps((prevState) => ({
      ...prevState,
      visible: true,
    }));
  };


  const handleChange = (event) => {
    setState((prevState) => ({
      ...prevState,
      veg: event,
    }));
    console.log(state);
  };

  const handlemeal = (event) => {
    console.log(event.item.props.children[1].props.children);
    setState((prevState) => ({
      ...prevState,
      meals: event.item.props.children[1].props.children,
    }));
    // getItems(state);
  };

  useEffect(() => {
    console.log(state);
    props.getItems(state);
  }, [state]);

  const selectedFood = (event) => {
    console.log(event.target);
  };

  return (
    <div>
      <UserLayout>
        <Row style={{ marginTop: "75px", justifyContent: "center" }}>
          <div className="site-card-border-less-wrapper">
            <Card style={{ borderLeft: "3px solid #74cf4e" }}>
              <h3>All deliveries</h3>

              <Row
                style={{
                  flexDirection: "row",
                  width: "600px",
                  alignItems: "center",
                }}
              >
                <Input
                  shape="round"
                  style={{ width: "250px", borderRadius: "21px" }}
                  placeholder="input search text"
                  //   enterButton="Search"
                  size="large"
                  //  onSearch={(value) => console.log(value)}
                />
                <Button
                  shape="round"
                  style={{
                    marginLeft: "46px",
                    backgroundColor: "#74cf4e",
                    color: "white",
                    borderColor: "#74cf4e",
                    border: 0,
                  }}
                  icon={<SearchOutlined />}
                >
                  Search
                </Button>
                <Switch
                  onClick={handleChange}
                  style={{
                    marginLeft: " 80px",
                    backgroundColor: "#74cf4e",
                    // width: " 77px",
                  }}
                  checkedChildren="veg"
                  unCheckedChildren="Non-veg"
                  defaultChecked
                />
              </Row>
              <Row>
                <Menu
                  mode="horizontal"
                  // style={{ boxShadow: "1px 4px 6px #888888", borderRadius: " 30px" }}
                  defaultSelectedKeys={["2"]}
                >
                  <Menu.Item
                    key="1"
                    style={{ height: "58px" }}
                    onClick={handlemeal}
                  >
                    <p> Breakfast</p>
                  </Menu.Item>
                  <Menu.Item
                    key="2"
                    style={{ height: "58px" }}
                    onClick={handlemeal}
                  >
                    <p>lunch</p>
                  </Menu.Item>
                  <Menu.Item
                    key="3"
                    style={{ height: "58px" }}
                    onClick={handlemeal}
                  >
                    <p>dinner</p>
                  </Menu.Item>
                  <Menu.Item
                    key="4"
                    style={{ height: "58px" }}
                    onClick={handlemeal}
                  >
                    <p>Snacks</p>
                  </Menu.Item>
                </Menu>
              </Row>
              <Row
                style={{
                  //  justifyContent: "space-between",
                  width: "600px",
                  flexFlow: "row wrap",
                  marginTop: "10px",
                }}
              >
                {props.menu.map((el) => (
                  <Link href="/posts/[fid]" as={`/posts/${el._id}`}>
                    <Card
                      hoverable
                      className="Styles.antCardBody"
                      bodyStyle={{
                        padding: "0",
                        fontSize: "9px",
                      }}
                      style={{ width: 117, marginLeft: "20px" }}
                      cover={<img alt="example" src="./images/plate.jpg" />}
                      key={el._id}
                    >
                      <p>{el.menu}</p>
                      <p>Special 1 plate</p>
                    </Card>
                  </Link>
                ))}
                {/* <Card
                  hoverable
                  className="Styles.antCardBody"
                  bodyStyle={{ padding: "0", fontSize: "9px" }}
                  style={{ width: 117 }}
                  cover={<img alt="example" src="./images/plate.jpg" />}
                >
                  <p>Roti,Daal,Rice</p>
                  <p>Special 1 plate</p>
                </Card>

                <Badge status="success">
                  <Card
                    hoverable
                    className="Styles.antCardBody"
                    bodyStyle={{ padding: "0", fontSize: "9px" }}
                    style={{ width: 117 }}
                    cover={<img alt="example" src="./images/plate.jpg" />}
                  >
                    {" "}
                    <p>Roti,Daal,Rice</p>
                    <p>Special 1 plate</p>
                  </Card>
                </Badge>
                <Card
                  hoverable
                  className="Styles.antCardBody"
                  bodyStyle={{ padding: "0", fontSize: "9px" }}
                  style={{ width: 117 }}
                  cover={<img alt="example" src="./images/plate.jpg" />}
                >
                  <p>Roti,Daal,Rice</p>
                  <p>Special 1 plate</p>
                </Card>
                <Card
                  hoverable
                  className="Styles.antCardBody"
                  bodyStyle={{ padding: "0", fontSize: "9px" }}
                  style={{ width: 117 }}
                  cover={<img alt="example" src="./images/plate.jpg" />}
                >
                  <p>Roti,Daal,Rice</p>
                  <p>Special 1 plate</p>
                </Card>
                <Card
                  hoverable
                  className="Styles.antCardBody"
                  bodyStyle={{ padding: "0", fontSize: "9px" }}
                  style={{ width: 117 }}
                  cover={<img alt="example" src="./images/plate.jpg" />}
                >
                  <p>Roti,Daal,Rice</p>
                  <p>Special 1 plate</p>
                </Card>
                <Card
                  hoverable
                  className="Styles.antCardBody"
                  bodyStyle={{ padding: "0", fontSize: "9px" }}
                  style={{ width: 117 }}
                  cover={<img alt="example" src="./images/plate.jpg" />}
                >
                  <p>Roti,Daal,Rice</p>
                  <p>Special 1 plate</p>
                </Card> */}
              </Row>
              {/* <Row style={{ marginTop: "12px" }}>
                <p>
                  <b style={{ marginRight: "7px" }}>Vender:</b>Naman
                </p>
              </Row> */}
              {/* <Orders /> */}
            </Card>
          </div>
        </Row>
      </UserLayout>
    </div>
  );
}

const mapStateToProps = (state) => ({
  menu: state.item.items,
});
export default connect(mapStateToProps, { getItems })(Food);
