import { GET_ITEMS, Post_Plan, Post_Add } from "../Actions/Types";

const initialState = {
  items: [],
  auth: false,
  PlanType: "",
  Address: null,
};

export default function (state = initialState, action) {
  console.log(action.type);
  switch (action.type) {
    case GET_ITEMS:
      return {
        ...state,
        items: action.payload,
      };
    case Post_Plan:
      return {
        ...state,
        PlanType: action.payload,
      };
    case Post_Add:
      return {
        ...state,
        Address: {...Address, ...action.payload},
      };
    default:
      return state;
  }
}
