                  mode="horizontal"
                  // style={{ boxShadow: "1px 4px 6px #888888", borderRadius: " 30px" }}
                  defaultSelectedKeys={["2"]}
                >
                  <Menu.Item
                    key="1"
                    style={{ height: "58px" }}
                    onClick={handlemeal}
                  >
                    <p> Breakfast</p>
                  </Menu.Item>
                  <Menu.Item
                    key="2"
                    style={{ height: "58px" }}
                    onClick={handlemeal}
                  >
                    <p>lunch</p>
                  </Menu.Item>
                  <Menu.Item
                    key="3"
                    style={{ height: "58px" }}
                    onClick={handlemeal}
                  >
                    <p>dinner</p>
                  </Menu.Item>
                  <Menu.Item
                    key="4"
                    style={{ height: "58px" }}
                    onClick={handlemeal}
