import { GET_ITEMS, Post_Plan, Post_Add } from "./Types";
import Axios from "axios";

export const getItems = (data) => {
  console.log(data);
  Axios.get(`/venders`).then((res) =>
    dispatch({
      type: GET_ITEMS,
      payload: res.data,
    })
  );
};

export const postPlan = (data) => (dispatch) => {
  console.log(data, "ddd");
  dispatch({
    type: Post_Plan,
    payload: data,
  });
};
export const postAddress = (data) => (dispatch) => {
  dispatch({
    type: Post_Add,
    payload: data,
  });
};
